import { Component, OnInit, ViewChild } from '@angular/core';
import { Calendar } from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid';

@Component({
  selector: 'app-calendar',
  templateUrl: './calendar.component.html',
  styleUrls: ['./calendar.component.css']
})
export class CalendarComponent implements OnInit {
  @ViewChild('calendar') calendarComponent: any;
  calendar: Calendar;

  constructor() { }

  ngOnInit(): void {
    this.calendar = new Calendar(this.calendarComponent.nativeElement, {
      plugins: [dayGridPlugin],
      initialView: 'dayGridMonth', // Afficher le mois par défaut
      events: [
        // Ajoutez vos événements de session ici
        // Par exemple : { title: 'Session 1', start: '2024-02-15', end: '2024-02-17' }
      ]
    });
    this.calendar.render();
  }
}
