import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { SessionService } from 'src/app/services/session.service'; 
import { DatePipe } from '@angular/common';

@Component({
  selector: 'app-addsession',
  templateUrl: './addsession.component.html',
  styleUrls: ['./addsession.component.css']
})
export class AddsessionComponent implements OnInit {
  constructor(
    private service : SessionService,
    private router : Router,
    private datePipe: DatePipe

  ) { }
  fournisseur:any={dateSession: null}

  ngOnInit(): void {


  }

  ajouter(): void {
    // Parse the string date into a Date object
    const parsedDate = new Date(this.fournisseur.dateSession);

    // Check if parsedDate is a valid Date object
    if (!isNaN(parsedDate.getTime())) {
      // Manually format the date string using toISOString()
      const formattedDate = parsedDate.toISOString();

      // Assign the formatted date to the fournisseur object
      this.fournisseur.dateSession = formattedDate;

      // Send the formatted date to the backend
      this.service.addSession(this.fournisseur).subscribe(
        data => {
          this.navigate();
        }
      );
    } else {
      console.error('Invalid date provided:', this.fournisseur.dateSession);
    }
  }
  navigate(){
    this.router.navigate(['sidebarr'])
  }


  }
