import {Component, OnInit} from '@angular/core';
import {OffreService} from "../services/offre.service";
import {Router} from "@angular/router";
import {Offre} from "../models/offre.model";

@Component({
  selector: 'app-offre',
  templateUrl: './offre.component.html',
  styleUrls: ['./offre.component.css']
})
export class OffreComponent implements OnInit{


  constructor(private offreService:OffreService,private router:Router){}
  ToShow:Offre[]=[];
  loadOffres() {
    this.offreService.getAll().subscribe(
      (data) => {
        this.ToShow = data;
      }
    );
  }
  ngOnInit(): void {
    this.loadOffres();
  }
}
<div class="home_content">
  <main>
    <app-sidebar></app-sidebar> <!-- Include your sidebar component -->

    <!-- Card layout -->
    <div class="card-container">
      <div class="card1">
        <div class="card-image"></div>
        <div class="card-text">
          <h2>Centre de données</h2>
          <p>Un centre de données est un ensemble informatique composé, entre autres,
             de serveurs, et permettant le stockage de données en un lieu sûr.</p>
        </div>
        <div class="card-stats">
          <div class="stat">
            <div class="value">Mot technique : Datacenter</div>
          </div>
        </div>
      </div>

      <!-- Repeat the card structure for other cards -->
      <!-- Example: -->
      <!-- <div class="card1"> ... </div> -->
    </div>
  </main>
</div>
